package com.wipro.java;

public class Flower {

	String name;
	int petals;
	
	Flower(String name,int petals){
		this.name=name;
		this.petals=petals;
	}
	
	void display() {
		System.out.println("flower name:"+name+"\npetals:"+petals);
	
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Flower flowerobj1=new Flower("rose",6);
		Flower flowerobj2=new Flower("sunflower",10);
		flowerobj1.display();
		flowerobj2.display();

	}

}
